namespace App\Addons\Messengers;
use Nullform\TelegramGateway\Client;
use Nullform\TelegramGateway\Parameters\SendVerificationMessageParameters;
use Nullform\TelegramGateway\AbstractException;

class Telegram
{

    public $alias = "telegram";
    public $data;

    public function __construct(){
        global $app;
        $this->data = $this->getData();
    }

    public function getData(){
        global $app;
        $data = $app->model->system_messenger_services->find("alias=?", [$this->alias]);
        if($data){
            $data->params = (object)_json_decode(decrypt($data->params));
            return $data;
        }
    }

    public function logo(){
        global $app;

        if(!$app->storage->name($this->data->image)->exist()){
            return 'data:image/png;base64,';
        }else{
            return $app->storage->name($this->data->image)->get();
        }

    }

    public function sendMessage($params=[]){
        global $app;

        $key = $this->data->params->bot_key;

        if($key && $params["chat_id"]){

            $paramsBuild = [
                "chat_id"=>$params["chat_id"],
                "parse_mode"=>"html",
                "text"=>$params["text"],
                "reply_markup"=>$params["keyboard"] ? _json_encode($params["keyboard"]) : null
            ];

            $result = _json_decode(curl("get","https://api.telegram.org/bot".$key."/sendMessage?".http_build_query($paramsBuild)));

            return ["status"=>true, "answer"=>$result];

        }

        return ["status"=>false];

    }

    public function sendVerifyCode($params=[]){
        global $app;

        $client = new Client($this->data->params->verification_key);

        try {

            $abilityStatus = $client->checkSendAbility($params["to"]);

            try {

                $parameters = new SendVerificationMessageParameters();
                $parameters->request_id = $abilityStatus->request_id;
                $parameters->code = $params["code"];

                $result = $client->sendVerificationMessage($params["to"], $parameters);

                return ["status"=>true];

            } catch (AbstractException $e) {

                logger("Messenger ".$this->alias." sendVerifyCode error: {$e->getMessage()}");
                return ["status"=>false];

            }

        } catch (AbstractException $e) {

            return ["status"=>false];

        }

        return ["status"=>false];

    }

    public function sendDocument($params=[]){
        global $app;

        if(!$params["filename"]){
            return ["status"=>false];
        }

        $result = _json_decode(curl("get","https://api.telegram.org/bot".$this->data->params->bot_key."/sendDocument", ['chat_id' => $params["chat_id"], 'document' => path($params["filename"], true), 'parse_mode' => 'html', 'caption' => $params["text"]]));

        return ["status"=>true, "answer"=>$result];

    }

    public function trigger($params=[]){
        global $app;

        if($this->data->params->bot_key){
            $this->setWebhook($this->data->params->bot_key);
        }

    }

    public function setWebhook($key){
        global $app;

        $result = _json_decode(curl("get","https://api.telegram.org/bot".$key."/setWebhook", ['url' => $app->system->buildWebhook("messenger", "telegram")]));

        return ["status"=>true, "answer"=>$result];

    }

    public function webhook($action=null){
        global $app;

        $data = file_get_contents('php://input');
        $data = _json_decode($data);

        if($data){

            $text = $data["message"]["text"];
            $chat_id = $data["message"]["chat"]["id"];

            if(mb_stripos($text,"/start") !== false) {
                $text = explode("/start", $text);
                if(trim($text[1])){
                    $user = $app->model->users->find("uniq_hash=?", [trim($text[1])]);
                    if($user){
                        if(!$user->messenger_token_id){
                            $app->model->users->update(["messenger_token_id"=>$chat_id], ["uniq_hash=?", [trim($text[1])]]);
                            $this->sendMessage(["chat_id"=>$chat_id, "text"=>translate("tr_af5b9c7d5341d1e7e2e32bf4273b44e3")]);
                        }else{
                            $this->sendMessage(["chat_id"=>$chat_id, "text"=>translate("tr_9191e20eed182a110c4aed8b53ad0bb3")]);
                        }
                    }
                }
            }

        }

    }

    public function outUserLinkBot($uniq_hash=null){
        global $app;

        return 'https://t.me/'.trim($this->data->params->bot_name, "@").'?start='.$uniq_hash;

    }

    public function fieldsForm($params=[]){
        global $app;

        return '
        <form class="integrationMessengerForm" >

            <h3>'.$this->data->name.'</h3>

            <div class="row">

                <div class="col-12 mb-3">

                  <label class="form-label mb-2" >'.translate("tr_55c9488fbbf51f974a38acd8ccb87ee1").'</label>

                  '.$app->ui->managerFiles(["filename"=>$this->data->image, "type"=>"images", "path"=>"images"]).'

                </div>

                <div class="col-12 mb-3">

                  <label class="form-label mb-2" >'.translate("tr_602680ed8916dcc039882172ef089256").'</label>

                  <input type="text" name="name" class="form-control" value="'.$this->data->name.'" />

                </div>

                <div class="col-12 mb-3">

                  <label class="form-label mb-2" >'.translate("tr_5e9de938e12a3985b583cdbe5e280c3f").'</label>

                  <input type="text" name="params[bot_name]" class="form-control" value="'.$this->data->params->bot_name.'" />

                </div>

                <div class="col-12 mb-3">

                  <label class="form-label mb-2" >Bot API KEY</label>

                  <input type="text" name="params[bot_key]" class="form-control" value="'.$this->data->params->bot_key.'" /> 

                </div>

                <div class="col-12">

                  <label class="form-label mb-2" >Verification API KEY</label>

                  <input type="text" name="params[verification_key]" class="form-control" value="'.$this->data->params->verification_key.'" /> 

                </div>

                <input type="hidden" name="id" value="'.$this->data->id.'" />

                <div class="mt-4 d-grid col-lg-6 mx-auto">
                  <button class="btn btn-primary buttonIntegrationMessengerSave">'.translate("tr_74ea58b6a801f0dce4e5d34dbca034dc").'</button>
                </div>

            </div>

        </from>
        ';

    }

}