class Liqpay{

    public $alias = "liqpay";
    public $data;

    public function __construct(){
        global $app;
        $this->data = $this->getData();
    }

    public function getData(){
        global $app;
        $data = $app->model->system_payment_services->find("alias=?", [$this->alias]);
        if($data){
            $data->params = (object)_json_decode(decrypt($data->params));
            return $data;
        }
    }

    public function logo(){
        global $app;

        if(!$app->storage->name($this->data->image)->exist()){
        return 'data:image/png;base64,';
        }else{
            return $app->storage->name($this->data->image)->get();
        }

    }

    public function createPayment($params=[]){
        global $app;

        try {
        
            $apiParams = [
                'public_key' => $this->data->params->public_key,
                'private_key' => $this->data->params->private_key
            ];

            $paymentParams = [
                'action' => 'pay',
                'amount' => numberFormat($params["amount"],2,".",""),
                'currency' => $this->data->params->currency,
                'description' => $params["title"],
                'order_id' => $params["order_id"],
                'server_url' => $app->system->buildWebhook("payment", $this->alias),
                'sandbox' => $this->data->params->sandbox,
                'version' => '3'
            ];

            $url = LiqPayCheckoutUrlCreator::create(
                $apiParams,
                $paymentParams
            );            

            return ["link"=>$url];

        } catch (\Exception $e) {
            logger("Payment ".$this->alias." createPayment error: {$e->getMessage()}");
        }

    }

    public function createPayout($data=[]){
        global $app;

        try {

            $liqpay = new LiqPayAPi(trim($this->data->params->public_key), trim($this->data->params->private_key));

            $result = $liqpay->api("request", [
                'action'         => 'p2pcredit',
                'version'        => '3',
                'amount'         => round($data["amount"],2),
                'currency'       => $this->data->params->currency,
                'description'    => $data["title"],
                'order_id'       => $data["order_id"],
                'receiver_card'  => $data["payment_data"]["score"],
                'receiver_last_name'  => $data["user_data"]["name"]
            ]);
        
            $result = _json_decode(_json_encode($result));

            if($result["result"] == "ok" && $result["status"] == "success"){

                $app->model->transactions_deals_payments->update(["comment"=>NULL, "status_processing"=>"done"], $data["id"]);
                
            }else{

                if($result["err_description"]){
                    $app->model->transactions_deals_payments->update(["comment"=>$result["err_description"], "status_processing"=>"payment_error", "user_show_error"=>0], $data["id"]);
                }else{
                    $app->model->transactions_deals_payments->update(["comment"=>translate("tr_ab2630021015478c54c9c5fa3cbb5b18"), "status_processing"=>"payment_error", "user_show_error"=>0], $data["id"]);
                }

            }

        } catch (Exception $e) {
            logger("Payment ".$this->alias." createPayout error: {$e->getMessage()}");
        }

    }

    public function createRefund($data=[]){
        global $app;

        try {

            $liqpay = new LiqPayAPi(trim($this->data->params->public_key), trim($this->data->params->private_key));

            $result = $liqpay->api("request", [
                'action'        => 'refund',
                'version'       => '3',
                'amount'        => round($data->amount,2),
                'order_id'      => $data->order_id
            ]);

            $result = _json_decode(_json_encode($result));

            if($result["result"] == "ok" && $result["status"] == "reversed"){
                $app->model->transactions_operations->update(["status_processing"=>"refund","refund_data"=>$refund ? encrypt(_json_encode($result)) : null], ["order_id=?", [$data->order_id]]);
            }else{
                $app->model->transactions_operations->update(["status_processing"=>"error","refund_data"=>$refund ? encrypt(_json_encode($result)) : null], ["order_id=?", [$data->order_id]]);
            }

        } catch (Exception $e) {
            logger("Payment ".$this->alias." createRefund error: {$e->getMessage()}");
        }

    }

    public function callback($action=null){
        global $app;

        try {

            $post_data = $_POST["data"];
            $signature = $_POST["signature"];

            $data = _json_decode(base64_decode($post_data));

            $sign = base64_encode( sha1( 
            trim($this->data->params->private_key) .  
            $post_data . 
            trim($this->data->params->private_key) 
            , 1 ));

            if($sign == $signature && ($data["status"] == "success" || $data["status"] == "wait_accept" || $data["status"] == "sandbox")){

                $order = $app->component->transaction->getOperation($data["order_id"]);

                if($order){
                    $app->component->transaction->callback($order->data, _json_encode($data));
                    header("HTTP/1.1 200 OK");
                    echo "ok";
                }else{
                    logger("Error payment not found order:".$data["order_id"]);
                    header("HTTP/1.1 404 ERROR");
                }

            }

        } catch (Exception $e) {
            logger("Payment ".$this->alias." callback error: {$e->getMessage()}");
            header("HTTP/1.1 404 ERROR");
        }

    }

